        });
        _heights.Add(new ChartData() {
            Name = "161-175",
            Count = _people.Count(p => p.Height is >= 161 and < 176)
        });
        _heights.Add(new ChartData() {
            Name = ">175",
            Count = _people.Count(p => p.Height is > 175)
        });
        
        _weights = new List<ChartData>();
